class Counter{constructor(t,e){this.counterName=t,this.initValue=e,this.counter=e,this.init()}init(){var t=parseInt(localStorage.getItem(this.counterName));this.counter=t||this.initValue}getValue(){return this.counter}increase(t){this.counter=parseInt(this.counter+t),localStorage.setItem(this.counterName,this.counter)}reset(){this.counter=parseInt(this.initValue),localStorage.setItem(this.counterName,this.counter)}}let counter=new Counter("btnClickCounter",0);const modals=document.querySelectorAll("[data-modal]");modals.forEach(o=>{o.addEventListener("click",t=>{t.preventDefault();const e=document.getElementById(o.dataset.modal),n=(e.classList.add("open"),counter.increase(1),console.log(counter.getValue()),e.querySelectorAll(".modal-exit"));n.forEach(t=>{t.addEventListener("click",t=>{t.preventDefault(),e.classList.remove("open")})})})});