var counter=localStorage.setItem("clickCounter","0");console.log(localStorage.getItem("clickCounter"));const modals=document.querySelectorAll("[data-modal");modals.forEach(l=>{l.addEventListener("click",e=>{e.preventDefault();e=parseInt(localStorage.getItem("clickCounter"));console.log(e),localStorage.setItem("clickCounter",++e);const t=document.getElementById(l.dataset.modal),o=(t.classList.add("open"),t.querySelectorAll(".modal-exit"));o.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),t.classList.remove("open")})})})});